INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6144, 'Разработчик', 'Разработчик — это максимально общее название специалистов, которые связаны с разработкой нового программного обеспечения, сайтов, платформ, экосистем, баз данных, технологических устройств и архитектуры. Они действуют в рамках своей роли в команде, но в целом способны сделать любой проект от стадии замысла до его реализации техническими средствами, Выберу в процессе обучения', 'https://gb.ru/geek_university/developer', 'Информационные технологии', null, null, 'Junior');
