INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6156, 'Инженер-аналитик ', 'Инженер-аналитик — это специалист совмещающий в себе несколько ролей: системного аналитика, бизнес-аналитика и инженера. В его задачи входят исследование, проектирование, конструирование и разработка новых технологий на базе анализа данных о пользователях, бизнес-процессах и продуктах компании., Аналитика', 'https://gb.ru/geek_university/developer/analyst', 'Информационные технологии', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6163, 'BI (Business Intelligence) аналитик ', 'BI-аналитик — это специалист, который использует данные и другую информацию, чтобы помочь организациям принимать обоснованные бизнес-решения. BI -аналитиков также могут попросить запрограммировать инструменты и модели данных, чтобы помочь сделать инфографику или создать инструменты, способные нормировать и контролировать данные., Аналитика', 'https://gb.ru/geek_university/developer/analyst/bi', 'Информационные технологии', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6203, 'Продуктовый аналитик ', 'Продуктовый аналитик ищет возможности для улучшения и развития продукта: исследует поведение клиентов, проверяет гипотезы, оценивает эффективность изменений продукта, Аналитика', 'https://gb.ru/geek_university/developer/analyst/product', 'Информационные технологии', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6247, 'Инженер-программист C++ ', 'Разработчик C++ создаёт программы, приложения, игры, операционные системы, драйверы и другое высоконагруженное программное обеспечение. Если вы хотите выжать максимум из любого «железа», программировать роботов, создать свою ОС, язык или другую мощную и жадную до ресурсов штуку — вам понадобится язык программирования С++, Программирование', 'https://gb.ru/geek_university/developer/programmer/cpp', 'Информационные технологии', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6215, 'Разработчик приложений на Android', 'Разработчики на Android занимаются созданием и доработкой приложений и программ для данной операционной системы. Они создают интерфейс, программируют логику работы приложения, занимаются оптимизацией ресурсов, которые необходимы для работы программы, Программирование', 'https://gb.ru/geek_university/developer/programmer/android', 'Информационные технологии', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6239, 'Инженер-программист ', 'Инженер-программист - это человек, который применяет принципы программной инженерии для проектирования, разработки, обслуживания, тестирования и оценки компьютерного программного обеспечения. Эта профессия подразумевает сочетание должностных обязанностей двух специальностей: программиста и инженера. Таким образом, инженер-программист является своего рода универсалом, объединяющим умения и навыки этих двух областей., Программирование', 'https://gb.ru/geek_university/developer/programmer', 'Информационные технологии', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6302, 'Инженер по тестированию', 'QA-тестировщик или инженер по тестированию — это специалист, который создаёт сценарии тестирования, прогнозирует и находит ошибки в IT продуктах. Таким специалистам важно разбираться в бизнес-процессах и досконально знать продукт, чтобы сохранять баланс между интересами пользователей, целями менеджмента компании и возможностями команды разработки., Тестирование', 'https://gb.ru/geek_university/developer/qa-engineer', 'Информационные технологии', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (5880, 'Программирование на Python в Minecraft: углублённый курс', ', Разработка игр', 'http://gb.ru/courses/geek-school/minecraft-python', 'Профессии для школьников', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6304, 'Инженер по тестированию Мастер', 'QA-тестировщик или инженер по тестированию — это специалист, который создаёт сценарии тестирования, прогнозирует и находит ошибки в IT продуктах. Таким специалистам важно разбираться в бизнес-процессах и досконально знать продукт, чтобы сохранять баланс между интересами пользователей, целями менеджмента компании и возможностями команды разработки., Тестирование', 'https://gb.ru/geek_university/developer/qa-engineer/master', 'Информационные технологии', null, null, 'Middle');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6261, 'Frontend-разработчик Мастер', 'Frontend-разработчик отвечает за внешний вид сайта или мобильного приложения: он верстает шаблоны, красиво оформляет текст и изображения, улучшает взаимодействие пользователя с интерфейсом. Без frontend-разработчика не получится создать удобный цифровой продукт., Программирование', 'https://gb.ru/geek_university/developer/programmer/frontend/master', 'Информационные технологии', null, null, 'Middle');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6265, 'Fullstack-разработчик', 'Fullstack-разработчики — универсальные программисты, которые разрабатывают и серверную, и клиентскую часть проекта. Эти специалисты очень востребованы, так как могут заменить программистов разного профиля. Fullstack-разработчики владеют широким стеком технологий, поэтому могут создать веб-проект в одиночку, Программирование', 'https://gb.ru/geek_university/developer/programmer/fullstack', 'Информационные технологии', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6277, 'Программист Java Мастер', 'Java-разработчик занимается разработкой высоконагруженных систем, веб-сервисов, банковских и корпоративных приложений, которыми пользуются люди по всему миру, Программирование', 'https://gb.ru/geek_university/developer/programmer/java/master', 'Информационные технологии', null, null, 'Middle');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6292, 'Инженер-программист Python Мастер', 'Python является самым популярным языком программирования из-за своей простоты и универсальности. Его используют не только для разработки веб-приложений и сервисов, но и для автоматизации, тестирования, анализа данных и машинного обучения. На Python написано множество библиотек для нейросетей и много другого., Программирование', 'https://gb.ru/geek_university/developer/programmer/python/master', 'Информационные технологии', null, null, 'Middle');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6340, 'ИТ-инженер Data Science Мастер', 'Data Scientist анализирует большой объём данных, прогнозирует события и помогает находить неочевидные закономерности. С помощью машинного обучения он может ставить диагнозы, обнаруживать болезни и персонализировать лечение., Выберу в процессе обучения', 'https://gb.ru/geek_university/engineer/datascience', 'Информационные технологии', null, null, 'Middle');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6411, 'Бренд-менеджер', 'Бренд-менеджер - это специалист, который формирует образ товара или компании. Он создаёт позиционирование и УТП бренда, выстраивает стратегию его развития и продвижения на рынке, а также формирует узнаваемость бренда и лояльность к нему среди потребителей. Для этого он проводит репутационный аудит, использует анализ рынка, конкурентов и целевой аудитории, различные каналы продвижения бренда, Tone of Voice, инструменты Digital PR и новых медиа. Бренд-менеджер управляет репутацией бренда и формирует стратегию ее развития., Бренд-менеджмент', 'https://gb.ru/geek_university/marketing/brand', 'Маркетинг', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6498, 'Графический дизайнер Мастер', 'Графический дизайнер — это специалист, который разрабатывает элементы графики и изображений, оформляет веб-ресурсы, создает фирменные стили брендов и другую визуальную продукцию. Это могут быть брендбуки, презентации, каталоги, логотипы, визитки. Для работы он использует графические редакторы и свои навыки: понимание композиции, колористики, типографики., Графический дизайн', 'https://gb.ru/geek_university/design/graphic/master', 'Дизайн', null, null, 'Middle');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6496, 'Графический дизайнер', 'Графический дизайнер оформляет и передает информацию так,  чтобы заинтересовать потребителя, вызвать у него определенные эмоции или объяснить ему что-то важное. Такой информацией могут быть книги, журналы, отчеты и другие публикации, упаковка продуктов, оформление социальных сетей, графика для рекламы, логотипы  и фирменные стили для компаний и многое другое. Все это графический дизайнер создает с помощью компьютерных программ и различных средств выразительности: текста и изображений, сочетаний цветов  и фигур, композиционных и стилистических приемов., Графический дизайн', 'https://gb.ru/geek_university/design/graphic-gb', 'Дизайн', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (5876, 'Веб-разработка: углублённый курс', ', Программирование', 'https://gb.ru/courses/geek-school/website-development', 'Профессии для школьников', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6506, 'Цифровой дизайнер Мастер', 'Цифровой дизайнер, он же веб-дизайнер, придумывает и проектирует интерфейсы для сайтов и веб-приложений так, чтобы они были удобными для пользователя и эффективно решали бизнес-задачи. Он креативен, на «ты» с инструментами дизайна, а также знает основы верстки и умеет работать с аналитикой., Цифровой дизайн', 'https://gb.ru/geek_university/design/digital/master', 'Дизайн', null, null, 'Middle');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6504, 'Цифровой дизайнер', 'Цифровой дизайнер создает визуальные активы для мультимедийных проектов: веб-сайтов, мобильных приложений, рекламы, анимации, социальных сетей, видеоигр, интерактивных дисплеев и многих других — уже существующих или только задуманных.   Для этого цифровой дизайнер работает в компьютерных программах, разбирается в особенностях разных платформ и пользуется теми же средствами выразительности, что и графический дизайнер: композицией, цветом, визуальными образами., Цифровой дизайн', 'https://gb.ru/geek_university/design/digital', 'Дизайн', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6500, 'Дизайнер интерьеров', 'Дизайнер среды — специалист, который трансформирует жилые и общественные пространства. Он умеет сочетать эстетику с эргономичностью и может спроектировать единое композиционное решение, которое отвечает запросам заказчика и требованиям функциональности., Дизайн среды', 'https://gb.ru/geek_university/design/interior-spec-gb', 'Дизайн', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (5579, 'Углублённый курс по анимации', ', Цифровое творчество', 'https://gb.ru/courses/geek-school/animation-pro', 'Профессии для школьников', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6475, 'Дизайнер', 'Дизайнер — это специалист в области разработки визуального оформления контента. Он предлагает заказчику несколько вариантов дизайна, которые максимально быстро и просто решают проблему клиента. Его работа обязательно включает в себя постановку цели, исследование целевой аудитории, создание объектов и продажу своего проекта. В дизайне много направлений и инструментов для создания различных проектов: от лендинга или логотипа до городской улицы или космического корабля., Выберу в процессе обучения', 'https://gb.ru/geek_university/design', 'Дизайн', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6397, 'Digital-маркетолог Специалист', 'Digital-маркетолог -  это профессионал в области взаимодействия бизнеса и клиента, который продвигает товары, услуги, бренды с помощью цифровых технологий. Для этого он использует SEO-продвижение, контекстную рекламу, Email-маркетинг, SMM, технологии работы с брендом, аналитику и стратегию. Digital-маркетолог делает возможным привлечение аудитории из цифрового пространства., Выберу в процессе обучения', 'https://gb.ru/geek_university/marketing/digital/specialist', 'Маркетинг', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6401, 'Интернет-маркетолог', 'Интернет-маркетолог - это специалист, который продвигает бизнес онлайн с помощью стека цифровых технологий. Инструменты интернет-маркетолога - SEO-продвижение, контекстная реклама, email-маркетинг, SMM. В процессе работы интернет-маркетолог анализирует статистику при помощи веб-сервисов и корректирует стратегию продвижения бизнеса., Интернет-маркетинг', 'https://gb.ru/geek_university/marketing/internet-gb', 'Маркетинг', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6416, 'Редактор', 'Редактор — это специалист, который создает текстовый материал. Инструменты редактора — контент-маркетинг, email-маркетинг, видеомаркетинг. В процессе работы редактор создает визуальную архитектуру контента в социальных сетях, оформляет ТЗ верстальщикам и дизайнерам или берет дело в свои руки., Интернет-маркетинг', 'https://gb.ru/geek_university/marketing/copywriter', 'Маркетинг', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6424, 'SEO-специалист', 'SEO-специалист продвигает бизнес онлайн в поисковых системах. Он использует инструменты поискового продвижения, проводит внутреннюю и внешнюю оптимизацию сайта, оптимизирует и анализирует трафик, обеспечивает поток качественных лидов. SEO-специалист использует сервисы веб-аналитики и корректирует стратегию продвижения бизнеса онлайн., Интернет-маркетинг', 'https://gb.ru/geek_university/marketing/seo', 'Маркетинг', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6406, 'Продуктовый маркетолог', 'Продакт-маркетолог — профессионал в области формулирования цифровых продуктов и вывода их на рынок средствами бизнеса и маркетинга. Профессия объединяет функционал менеджера продукта и маркетолога для получения результата — жизнеспособный и востребованный продукт на рынке. Специалисты используют управленческие инструменты: формулирование гипотез, налаживание взаимодействия и между разработчиками продукта и бизнесом. Строят маркетинговую стратегию вывода на рынок, занимаются аналитикой, адаптацией и развитием бизнеса под запросы рынка., Продуктовый маркетинг', 'https://gb.ru/geek_university/marketing/product', 'Маркетинг', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6420, 'SMM-менеджер', 'SMM-менеджер — это специалист, который продвигает проекты и бренды в соцсетях. Он создает тексты, подбирает изображения и видео для постов, формирует контент-план и может разработать SMM-стратегию с нуля. В процессе работы SMM-менеджер анализирует эффективность создаваемого контента и обратную связь., Интернет-маркетинг', 'https://gb.ru/geek_university/marketing/smm-management', 'Маркетинг', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6466, 'Perfomance менеджер', 'Performance-менеджер — это специалист, который продвигает бизнес на рынок по платным каналам. Инструменты performance-менеджера — это контекстная реклама, платный поиск и программатик-системы. В процессе работы он создает стратегию продвижения в платных поисковых каналах, анализирует данные рекламных кампаний и оптимизирует их бюджеты., Продуктовый маркетинг', 'https://gb.ru/geek_university/marketing/performance-manager', 'Маркетинг', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6428, 'Таргетолог', 'Таргетолог — это один из ключевых специалистов в команде маркетинга. Он приводит на сайт компании пользователей, заинтересованных в покупке. Инструменты таргетолога — рекламный кабинет VК, TikTok, «Дзен», Яндекс.Метрика. В процессе работы он изучает и сегментирует целевую аудиторию, запускает рекламные кампании и анализирует их эффективность., Интернет-маркетинг', 'https://gb.ru/geek_university/marketing/targeting', 'Маркетинг', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6432, 'Специалист по работе с маркетплейсами', 'Менеджер по работе с маркетплейсами — это специалист, который продвигает товары на маркетплейсах при помощи встроенных инструментов и маркетинговых технологий. Его инструменты — личный кабинет маркетплейса, визуальные редакторы и сервисы подбора ключевых слов. В процессе работы он составляет перечень товаров и ассортиментную матрицу, выбирает наиболее выгодный товар, рассчитывает оптимальную цену и продумывает скидочную политику., Интернет-маркетинг', 'https://gb.ru/geek_university/marketing/e-commerce', 'Маркетинг', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6644, 'Дата-сайентист в медицине Мастер', 'Data Scientist анализирует большой объём данных, прогнозирует события и помогает находить неочевидные закономерности. С помощью машинного обучения он создает инструменты для постановки диагнозов, обнаружения болезней и персонализации лечения., Выберу в процессе обучения', 'https://gb.ru/geek_university/developer/analyst/data-science-medicine', 'Информационные технологии', null, null, 'Middle');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6648, 'Сетевой инженер ', 'Инженер по сетевым коммуникациям востребован в компаниях с развитой IT-инфраструктурой: это провайдеры, мобильные операторы, банки, больницы, заводы и аэропорты. Сетевой инженер создаёт и обслуживает внутренние сети компании, следит за устойчивой работой, правильной конфигурацией и защищённостью сетей., Архитектура', 'https://gb.ru/geek_university/developer/architecture/network-engineer', 'Информационные технологии', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6652, 'DevOps-инженер Мастер', 'DevOps-инженеры контролируют все этапы создания продукта: от написания кода до релиза. Помогают отделам разработки и администрирования, синхронизируют их усилия и автоматизируют технические процессы. За два года спрос на этих специалистов вырос на 70%. Мы поможем стать DevOps-инженером с нуля и найдём вам работу, Архитектура', 'https://gb.ru/geek_university/developer/architecture/devops', 'Информационные технологии', null, null, 'Middle');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6656, 'Системный и бизнес аналитик', 'Системный аналитик помогает оптимизировать и автоматизировать работу компании и её подразделений. Этот специалист разбирается в менеджменте, экономике и информационных технологиях и помогает скоординировать процесс разработки ПО так, чтобы результат был максимально полезен для бизнеса, Аналитика', 'https://gb.ru/geek_university/developer/analyst/system-business', 'Информационные технологии', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6524, 'Ландшафтный дизайнер', 'Ландшафтный дизайн — это визуальный язык, позволяющий работать с природными средствами. Ландшафтный дизайнер сочетает три аспекта (архитектуру, ботанику и философию) в зависимости от того, как будет использоваться территория, которую он облагораживает., Дизайн среды', 'https://gb.ru/geek_university/design/landscape', 'Дизайн', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6540, 'UX/UI дизайнер Мастер', 'UX/UI-дизайнер проектирует взаимодействие пользователя с сайтом, приложением или сервисом. Он создаёт визуальные элементы, систему и прототип интуитивно понятного интерфейса., Цифровой дизайн', 'https://gb.ru/geek_university/design/interface', 'Дизайн', null, null, 'Middle');
