INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6783, e'1С-разработчик	', 'Программист 1С (или 1С разработчик) – профессия на стыке программирования, бухгалтерского учета и менеджмента. Программисты 1С занимаются установкой, настройкой и доработкой бухгалтерских программ семейства 1С, ERP-систем семейства 1С-Предприятие и других продуктов фирмы 1С, Программирование', 'https://gb.ru/geek_university/developer/programmer/1c-developer', 'Информационные технологии', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6794, 'Информационная безопасность', 'Специалист по информационной безопасности — это профессионал, который обеспечивает конфиденциальность любой информации, касающейся как самой компании, так и её сотрудников. Также этот человек предотвращает любые утечки информации и минимизирует угрозы информационным системам компании., Информационная безопасность (смешанная)', 'https://gb.ru/geek_university/developer/qa-engineer/security', 'Информационные технологии', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (6870, 'Разработчик для школьников', 'Разработчик — это максимально общее название специалистов, которые связаны с разработкой нового программного обеспечения, сайтов, платформ, экосистем, баз данных, технологических устройств и архитектуры. Они действуют в рамках своей роли в команде, но в целом способны сделать любой проект от стадии замысла до его реализации техническими средствами, Выберу в процессе обучения', 'https://gb.ru/geek_university/developer/obuchenie-it-dlya-shkolnikov', 'Профессии для школьников', null, null, 'Junior');
