INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (8285, 'Контекстная реклама. Специалист', 'Специалист по контекстной рекламе продвигает товары и услуги в интернете. Он запускает рекламные кампании, задает и оптимизирует бюджет, анализирует результаты. Помогает создавать крутые рекламные креативы, цепляющие тексты и вовлекающие лендинги. Такой специалист может в разы увеличить прибыль бизнеса, поэтому профессия становится на рынке все более востребованной., Интернет-маркетинг', 'https://gb.ru/geek_university/marketing/context-spec', 'Маркетинг', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (8688, 'Разработчик Игр на Unity', 'Разработчик на Unity отвечает за создание игровых механик, уровней и оптимизацию игрового процесса. Он работает с языком программирования C# и визуальным редактором Unity Editor. На Unity создают игры для консолей, мобильных устройств и ПК., Программирование', 'https://gb.ru/geek_university/developer/programmer/unity-gb', 'Информационные технологии', null, null, 'Junior');
INSERT INTO public."Program" (id, name, speciality, url, tag, days_amount, price, difficulty) VALUES (8458, 'Трафик-менеджер. Специалист', 'Трафик-менеджер продвигает товары и услуги в интернете при помощи платных каналов привлечения. Он запускает рекламные кампании, задает и оптимизирует бюджет, анализирует результаты. В результате бизнес получает новых клиентов с минимальными затратами на рекламу. Трафик-менеджер создает крутые  креативы и цепляющие тексты. Такой специалист может в разы увеличить прибыль бизнеса, поэтому профессия востребована на рынке., Выберу в процессе обучения', 'https://gb.ru/geek_university/marketing/traffic-spec', 'Маркетинг', null, null, 'Junior');
